#' @export
collect.duckplyr_df <- function(x, ...) {
  # Side effect: enable materialization if it was disabled
  duckdb$rel_from_altrep_df(x, enable_materialization = TRUE)
  # Side effect: ALTREP materialization is triggered
  nrow(x)
  x
}

duckplyr_collect <- function(x, ...) {
  try_fetch(
    x <- as_duckplyr_df(x),
    error = function(e) {
      testthat::skip(conditionMessage(e))
    }
  )
  out <- collect(x, ...)
  class(out) <- setdiff(class(out), "duckplyr_df")
  out
}
